def split_long_sentence(sentence: str, limit: int) -> list[str]:
    parts: list[str] = []
    atoms = re.split(r'(?<=[;,:])\s+', sentence)
    buf = ""
    for a in atoms:
        a = a.strip()
        if not a:
            continue
        if len(a) > limit:
            i = 0
            while i < len(a):
                end = min(i + limit, len(a))
                cut = max(a.rfind(" ", i, end), a.rfind("\n", i, end))
                if cut == -1 or cut <= i + int(limit * 0.6):
                    cut = end
                chunk = a[i:cut].strip()
                if chunk:
                    if buf and len(buf) + 1 + len(chunk) <= limit:
                        buf = f"{buf} {chunk}"
                    else:
                        if buf:
                            parts.append(buf)
                        buf = chunk
                i = cut
            continue
        if buf and len(buf) + 1 + len(a) <= limit:
            buf = f"{buf} {a}"
        else:
            if buf:
                parts.append(buf)
            buf = a
    if buf:
        parts.append(buf)
    return parts

def smart_split_paragraph(paragraph: str, limit: int) -> list[str]:
    sentences = re.split(r'(?<=[\.\!\?\â€¦])\s+', paragraph.strip())
    chunks: list[str] = []
    buf = ""
    for s in sentences:
        s = s.strip()
        if not s:
            continue
        if len(s) > limit:
            for piece in split_long_sentence(s, limit):
                if len(piece) <= limit:
                    if buf and len(buf) + 1 + len(piece) <= limit:
                        buf = f"{buf} {piece}"
                    else:
                        if buf:
                            chunks.append(buf)
                        buf = piece
                else:
                    for i in range(0, len(piece), limit):
                        seg = piece[i:i+limit]
                        if buf:
                            chunks.append(buf)
                        buf = seg
            continue
        if buf and len(buf) + 1 + len(s) <= limit:
            buf = f"{buf} {s}"
        else:
            if buf:
                chunks.append(buf)
            buf = s
    if buf:
        chunks.append(buf)
    return chunks

def split_text_for_telegram(text: str, limit: int = LIMIT) -> list[str]:
    paragraphs = re.split(r'\n{2,}', text.strip())
    messages: list[str] = []
    buf = ""
    def flush():
        nonlocal buf
        if buf:
            messages.append(buf.rstrip())
            buf = ""
    for p in paragraphs:
        p = p.strip()
        if not p:
            continue
        sep = "\n\n" if buf else ""
        if len(p) <= limit:
            if len(buf) + len(sep) + len(p) <= limit:
                buf = f"{buf}{sep}{p}"
            else:
                flush(); buf = p
        else:
            parts = smart_split_paragraph(p, limit)
            for part in parts:
                if len(part) <= limit:
                    if len(buf) + len(sep) + len(part) <= limit:
                        buf = f"{buf}{sep}{part}"
                    else:
                        flush(); buf = part
                else:
                    for i in range(0, len(part), limit):
                        piece = part[i:i+limit]
                        if len(buf) + len(sep) + len(piece) <= limit:
                            buf = f"{buf}{sep}{piece}"
                        else:
                            flush(); buf = piece
        sep = ""
    flush()
    return messages

